CREATE TABLE jelvix_news (id BIGINT AUTO_INCREMENT, title VARCHAR(255) NOT NULL, logo VARCHAR(255), text TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE jelvix_project_category (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE jelvix_projects (id BIGINT AUTO_INCREMENT, category_id BIGINT NOT NULL, title VARCHAR(255) NOT NULL, thumbnail VARCHAR(255), photo VARCHAR(255), description MEDIUMTEXT, ios_link VARCHAR(255), android_link VARCHAR(255), other_link VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX category_id_idx (category_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE jelvix_services (id BIGINT AUTO_INCREMENT, category_id BIGINT NOT NULL, title VARCHAR(255) NOT NULL, text TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX category_id_idx (category_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE jelvix_services_category (id BIGINT AUTO_INCREMENT, name VARCHAR(128) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
ALTER TABLE jelvix_projects ADD CONSTRAINT jelvix_projects_category_id_jelvix_project_category_id FOREIGN KEY (category_id) REFERENCES jelvix_project_category(id) ON DELETE CASCADE;
ALTER TABLE jelvix_services ADD CONSTRAINT jelvix_services_category_id_jelvix_services_category_id FOREIGN KEY (category_id) REFERENCES jelvix_services_category(id) ON DELETE CASCADE;
